import {useState} from 'react';

function App(){
  const [todos,setTodos]=useState(["Apple","Banana"]);

  const [isChecked,setCheck]=useState(false);
  const [selectedIndex,setIndex]=useState(-1);
  const handleDelete=(indexDel)=>{
    setTodos(todos.filter((_,index)=>index!==indexDel));
  }
  return(
    <>
    <ul style={{listStyle:'none'}}>
     { todos.map((item,index) => 
        <div style={{display:'flex',flexDirection:'row'}}>
          <input type='checkbox' onChange={() => {
            setCheck(!isChecked);
            setIndex(index);
          }
          }/>
          <li key={index} style={{textDecoration:isChecked && selectedIndex===index && 'line-through'}}>Learn {item}</li>
          <button className='edit'>Edit</button>
          <button className='delete' onClick={handleDelete}>Delete</button>
        </div>
      ) }
    </ul>
    </>
  )
}

export default App;
