import { useState } from 'react';

function App() {
  const [todos, setTodos] = useState(["Apple", "Banana"]);
  localStorage.setItem(todos,JSON.stringify(todos))
  const [isChecked, setCheck] = useState(false);
  const [selectedIndex, setIndex] = useState(-1);

  const handleDelete = (indexDel) => {
    setTodos(todos.filter((_, index) => index !== indexDel));
    localStorage.setItem(todos,JSON.stringify(todos))
  };

  return (
    <>
      <ul style={{ listStyle: 'none' }}>
        {todos.map((item, index) =>
          <div key={index} style={{ display: 'flex', flexDirection: 'row', alignItems: 'center', marginBottom: '5px' }}>
            <input
              type='checkbox'
              onChange={() => {
                setCheck(!isChecked);
                setIndex(index);
              }}
            />
            <li style={{ textDecoration: isChecked && selectedIndex === index ? 'line-through' : 'none', marginLeft: '8px', marginRight: '8px' }}>
              Learn {item}
            </li>
            <button className='edit'>Edit</button>
            <button className='delete' onClick={() => handleDelete(index)}>Delete</button>
          </div>
        )}
      </ul>
    </>
  );
}

export default App;
