import { useState, useEffect } from 'react';

function App() {
  const [todos, setTodos] = useState(() => {
    // Load from localStorage when component mounts
    const savedTodos = localStorage.getItem("todos");
    return savedTodos ? JSON.parse(savedTodos) : [];
  });

  const [isChecked, setCheck] = useState(false);
  const [selectedIndex, setIndex] = useState(-1);
  const [newItem,setNewItem]=useState("");

  // Save to localStorage whenever todos changes
  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const handleDelete = (indexDel) => {
    setTodos(todos.filter((_, index) => index !== indexDel));
  };

  const handleAdd=()=>{
    setTodos([...todos,newItem]);
    setNewItem("");
  }
  return (
    <>
    <input type='text' value={newItem} onChange={(e)=> setNewItem(e.target.value)}/>
    <button onClick={handleAdd}>Add</button>
      <ul style={{ listStyle: 'none' }}>
        {todos.map((item, index) =>
          <div key={index} style={{ display: 'flex', flexDirection: 'row', alignItems: 'center', marginBottom: '5px' }}>
            <input
              type='checkbox'
              onChange={() => {
                setCheck(!isChecked);
                setIndex(index);
              }}
            />
            <li style={{ textDecoration: isChecked && selectedIndex === index ? 'line-through' : 'none', marginLeft: '8px', marginRight: '8px' }}>
              Learn {item}
            </li>
            <button className='edit'>Edit</button>
            <button className='delete' onClick={() => handleDelete(index)}>Delete</button>
          </div>
        )}
      </ul>
    </>
  );
}

export default App;
