import { useState, useEffect } from 'react';

function App() {
  const [todos, setTodos] = useState(() => {
    const savedTodos = localStorage.getItem("todos");
    return savedTodos ? JSON.parse(savedTodos) : [];
  });

  const [newItem, setNewItem] = useState("");
  const [editIndex, setEditIndex] = useState(-1);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const handleAdd = () => {
    if (newItem.trim() !== "") {
      setTodos([...todos, { text: newItem, completed: false }]);
      setNewItem("");
    }
  };

  const handleDelete = (indexDel) => {
    setTodos(todos.filter((_, index) => index !== indexDel));
  };

  const toggleComplete = (index) => {
    setTodos(
      todos.map((todo, i) =>
        i === index ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  return (
    <>
      <input
        type='text'
        value={newItem}
        onChange={(e) => setNewItem(e.target.value)}
      />
      <button onClick={handleAdd}>Add</button>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {todos.map((todo, index) => (
          <div
            key={index}
            style={{
              display: 'flex',
              flexDirection: 'row',
              alignItems: 'center',
              marginBottom: '5px'
            }}
          >
            <input
              type='checkbox'
              checked={todo.completed}
              onChange={() => toggleComplete(index)}
            />

            {editIndex === index ? (
              <input
                type="text"
                defaultValue={todo.text}
                onBlur={(e) => {
                  setTodos(
                    todos.map((t, i) =>
                      i === index ? { ...t, text: e.target.value } : t
                    )
                  );
                  setEditIndex(-1);
                }}
                autoFocus
                style={{ marginLeft: '8px', marginRight: '8px' }}
              />
            ) : (
              <li
                style={{
                  textDecoration: todo.completed ? 'line-through' : 'none',
                  marginLeft: '8px',
                  marginRight: '8px'
                }}
              >
                Learn {todo.text}
              </li>
            )}

            <button className='edit' onClick={() => setEditIndex(index)}>Edit</button>
            <button className='delete' onClick={() => handleDelete(index)}>Delete</button>
          </div>
        ))}
      </ul>
    </>
  );
}

export default App;
